import React, { Component, Fragment } from 'react'
import { Col, Container, Row } from 'react-bootstrap'
import "slick-carousel/slick/slick.css" 
import "slick-carousel/slick/slick-theme.css"
import Slider from "react-slick";
import RestClient from '../../RestAPI/RestClient';
import AppUrl from '../../RestAPI/AppUrl';


class ClientReview extends Component {
        constructor(){
          super();
          this.state={
            clientData:[]
          }
      }
      componentDidMount(){
              RestClient.GetRequest(AppUrl.ClientReview).then(result=>{
                  this.setState({clientData:result});
              })
      }
     render() {
      const clientList = this.state.clientData;
      const clientView = clientList.map(clientList=>{
        return    <div>
          <Row className="text-center justify-content-center">
               <Col lg={6} md={6} sm={12}>
       <img className="circleImg" src={clientList.client_img} />
       <h2 className="reviewName">{MyList.client_title}</h2>
      <p className="reviewDescription">{MyList.client_description}</p>

               </Col>
          </Row>
          </div>  

      })
         var settings = {
               autoplaySpeed:3000,
               autoplay:true,
               dots: true,
               infinite: true,
               speed: 1000,
               arrows:false,
               horizontal:true,
               horizontalSwiping:true,
               slidesToShow: 1,
               slidesToScroll: 1,
               initialSlide: 1,
               responsive: [
                 {
                   breakpoint: 1024,
                   settings: {
                     slidesToShow: 1,
                     slidesToScroll: 1,
                     infinite: true,
                     dots: true
                   }
                 },
                 {
                   breakpoint: 600,
                   settings: {
                     slidesToShow: 1,
                     slidesToScroll: 1,
                     initialSlide: 1
                   }
                 },
                 {
                   breakpoint: 480,
                   settings: {
                     slidesToShow: 1,
                     slidesToScroll: 1
                   }
                 }
               ]
             };




          return (
            <Fragment>
            <Container fluid={true} className="siderBack text-center">

                <h1 className="reviewMainTitle p-3">TESTIMOIAL</h1>
                <div className="reviewbottom"></div> 

                <Slider {...settings}>
                    <div>
                        <Row className="text-center justify-content-center">
                            <Col lg={6} md={6} sm={12}>
                             <img className="circleImg" alt="" src="https://image.freepik.com/free-photo/handsome-young-man-with-new-stylish-haircut_176420-19637.jpg" />
                             <h2 className="reviewName">Kazi Ariyan</h2>
                             <p className="reviewDescription">Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500</p>
                            </Col>
                        </Row>
                    </div>    


                </Slider>
            </Container>
            </Fragment>
          )
     }
}

export default ClientReview
